targets.expr:
sum(
  rate(
    nginx_ingress_controller_requests{status!~"[4-5].*", clusterName=~"$cluster_name", controller_class=~"$controller_class", ingress=~"$ingress", namespace=~"$namespace", controller_pod=~"$pod", host=~"$host", path=~"$path"}[${__range_s}s]
      )
   )   
/ 
(
  sum(
    rate(
      nginx_ingress_controller_requests{clusterName=~"$cluster_name", controller_class=~"$controller_class", ingress=~"$ingress", namespace=~"$namespace", controller_pod=~"$pod", host=~"$host", path=~"$path"}[${__range_s}s]
        )
     ) - 
  (
  sum(
    rate(
      nginx_ingress_controller_requests{status=~"404|499", clusterName=~"$cluster_name", controller_class=~"$controller_class", ingress=~"$ingress",namespace=~"$namespace", controller_pod=~"$pod", host=~"$host", path=~"$path"}[${__range_s}s]
        )
     ) 
  or vector(0)
  )
)

